import React, { useEffect, useRef } from 'react';
import './BrandCarousel.css';

const brands = [
  { id: 1, src: 'data:image/png;base64,', alt: 'Wild Stone' },
  { id: 2, src: 'data:image/png;base64,', alt: 'Plum' },
  { id: 3, src: 'data:image/png;base64,', alt: 'Nivea' },
  { id: 4, src: 'data:image/png;base64,', alt: 'Himalaya' },
  { id: 5, src: 'https://upload.wikimedia.org/wikipedia/commons/thumb/2/29/Xiaomi_logo.svg/2560px-Xiaomi_logo.svg.png', alt: 'Xiaomi' },
  { id: 6, src: 'data:image/jpeg;base64,', alt: 'Bata' },
  { id: 7, src: 'data:image/png;base64,', alt: 'Woosh' },
];

const BrandCarousel = () => {
  const scrollRef = useRef(null);

  useEffect(() => {
    const scrollContainer = scrollRef.current;
    let scrollAmount = 0;
    const scrollStep = 1;
    const scrollInterval = 20;

    const scroll = () => {
      if (scrollContainer) {
        scrollAmount += scrollStep;
        if (scrollAmount >= scrollContainer.scrollWidth / 2) {
          scrollAmount = 0;
        }
        scrollContainer.scrollLeft = scrollAmount;
      }
    };

    const intervalId = setInterval(scroll, scrollInterval);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="brand-carousel" ref={scrollRef}>
      <div className="brand-carousel-track">
        {brands.concat(brands).map((brand, index) => (
          <div className="brand-carousel-item" key={index}>
            <img src={brand.src} alt={brand.alt} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default BrandCarousel;
